import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';

export interface SubscriptionPlan {
  id: string;
  name: string;
  price: number;
  billingCycle: 'monthly' | 'yearly';
  features: string[];
  isActive: boolean;
}

@Injectable({
  providedIn: 'root'
})
export class SubscriptionService {
  private subscriptionPlans: SubscriptionPlan[] = [
    {
      id: 'free',
      name: 'Free Plan',
      price: 0,
      billingCycle: 'monthly',
      features: [
        'Single Line Predictions',
        'Basic Result History',
        'Standard Support'
      ],
      isActive: true
    },
    {
      id: 'pro',
      name: 'Pro Plan',
      price: 19.99,
      billingCycle: 'monthly',
      features: [
        'Single Line Predictions',
        'Bulk CSV Predictions',
        'Advanced Analytics',
        'Priority Support',
        'Unlimited History'
      ],
      isActive: false
    }
  ];

  private currentSubscriptionSubject = new BehaviorSubject<string>('free');
  currentSubscription$ = this.currentSubscriptionSubject.asObservable();

  constructor() {
    // Check for existing subscription in localStorage
    const savedSubscription = localStorage.getItem('currentSubscription');
    if (savedSubscription) {
      this.currentSubscriptionSubject.next(savedSubscription);
    }
  }

  getSubscriptionPlans(): SubscriptionPlan[] {
    return this.subscriptionPlans;
  }

  getCurrentSubscription(): string {
    return this.currentSubscriptionSubject.value;
  }

  setSubscription(planId: string): void {
    this.currentSubscriptionSubject.next(planId);
    localStorage.setItem('currentSubscription', planId);
    
    // Update active state of plans
    this.subscriptionPlans.forEach(plan => {
      plan.isActive = plan.id === planId;
    });
  }

  hasBulkAccess(): boolean {
    const currentPlan = this.getCurrentSubscription();
    return currentPlan === 'pro';
  }

  // Mock payment processing - in a real app this would connect to a payment gateway
  processPurchase(planId: string, paymentDetails: any): Observable<boolean> {
    return new Observable<boolean>(observer => {
      // Simulate API call delay
      setTimeout(() => {
        // Success condition (always succeeds in this mock)
        this.setSubscription(planId);
        observer.next(true);
        observer.complete();
      }, 1500);
    });
  }
}
