commits.content:
:host {
  .legend {
    small {
      font-size: x-small;
    }
  }
}
.dashboard-container {
  width: 95%;
  max-width: 1800px;
  margin: 0 auto;
  padding: 1rem;
}

/* Make sure the cards inside expand fully */
.dashboard-container c-card {
  width: 100%;
}

/* Make sure tables and charts use full width */
c-card-body {
  width: 100%;
}

/* Fix for radar chart size */
c-chart {
  display: block;
  width: 100%;
  min-height: 300px;
}

/* Make sure data tables use full width */
table {
  width: 100%;
}

/* Add some breathing room to table cells */
td, th {
  padding: 8px;
}

// Add to dashboard.component.scss
.table-responsive {
  position: relative;
  
  table {
    margin-bottom: 0;
  }
  
  thead th {
    position: sticky;
    top: 0;
    background-color: white;
    z-index: 1;
    // Add a subtle shadow to visually separate the header
    box-shadow: 0 2px 2px -1px rgba(0, 0, 0, 0.1);
  }
}

// Fix Bootstrap hover styles for sticky headers
.table-hover tbody tr:hover {
  background-color: rgba(0, 0, 255, 0.05);
}

.categories-summary {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  max-width: 150px;
  cursor: help;
  
  .badge {
    display: inline-block;
    padding: 0.25em 0.4em;
    border-radius: 0.25rem;
    color: white;
    text-align: center;
    white-space: nowrap;
    vertical-align: baseline;
  }
}

// Make the tooltip show on hover
[title] {
  position: relative;
  
  &:hover::after {
    content: attr(title);
    position: absolute;
    bottom: 100%;
    left: 0;
    background: rgba(0, 0, 0, 0.8);
    color: white;
    padding: 5px 10px;
    border-radius: 4px;
    font-size: 0.75rem;
    z-index: 100;
    white-space: pre;
    min-width: 150px;
  }
}

/* Override container styles to fix display issues */
:host ::ng-deep c-container {
  display: flex !important;
  flex-direction: column;
  width: 100%;
}

/* If the above doesn't work, try targeting more specifically */
:host ::ng-deep .container-lg {
  display: flex !important;
  flex-direction: column;
  width: 100%;
}
