.subscription-card {
  transition: transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out;
  
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 20px rgba(0,0,0,0.1);
  }
  
  &.border-primary {
    border-width: 2px;
  }
}

.pro-plan {
  position: relative;
  
  // Add a slight scale effect for the recommended plan
  transform: scale(1.03);
  z-index: 1;
  
  &:hover {
    transform: translateY(-5px) scale(1.03);
  }
}

.price-tag {
  margin: 1rem 0;
  
  .currency {
    font-size: 1.2rem;
    vertical-align: top;
    position: relative;
    top: 0.5rem;
  }
  
  .amount {
    font-size: 3.5rem;
    font-weight: 700;
  }
  
  .period {
    font-size: 1rem;
    color: #6c757d;
  }
}

.feature-list {
  list-style-type: none;
  padding-left: 0;
  margin-bottom: 0;
  
  li {
    padding: 0.5rem 0;
    border-bottom: 1px solid rgba(0,0,0,0.05);
    
    &:last-child {
      border-bottom: none;
    }
  }
}
